import * as React from "react";
import PropTypes from "prop-types";
import AppBar from "@mui/material/AppBar";
import Box from "@mui/material/Box";
import Divider from "@mui/material/Divider";
import Drawer from "@mui/material/Drawer";
import IconButton from "@mui/material/IconButton";
import TextField from "@mui/material/TextField";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemButton from "@mui/material/ListItemButton";
import ListItemText from "@mui/material/ListItemText";
import MenuIcon from "@mui/icons-material/Menu";
import Toolbar from "@mui/material/Toolbar";
import Typography from "@mui/material/Typography";
import Button from "@mui/material/Button";
import { Select } from "@mui/material";
import ReactFlagsSelect from "react-flags-select";
import { useState } from "react";
const drawerWidth = 240;
const navItems = ["Türkçe", "Giriş Yap", "Kayıt ol"];

function Header(props) {
  const [selected, setSelected] = useState("TR");
  const phones = {
    US: "+1",
    TR: "+90",
    DE: "+49",
    IT: "+39",
    IN: "+91",
  };

  const { window } = props;
  const [mobileOpen, setMobileOpen] = React.useState(false);

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };

  const drawer = (
    <Box onClick={handleDrawerToggle} sx={{ textAlign: "center" }}>
      <Typography variant="h6" sx={{ my: 2 }}>
        <img
          data-testid="main-image"
          src="data:image/svg+xml;base64,"
          alt="GetirYemek"
          shape="NORMAL"
          class="style__Image-sc-__sc-1h9bp23-0 ezAmPr"
        ></img>
      </Typography>
      <Divider />
      <List>
        {navItems.map((item) => (
          <ListItem key={item} disablePadding>
            <ListItemButton sx={{ textAlign: "center" }}>
              <ListItemText primary={item} />
            </ListItemButton>
          </ListItem>
        ))}
      </List>
    </Box>
  );

  const container =
    window !== undefined ? () => window().document.body : undefined;

  return (
    <Box sx={{ display: "flex" }}>
      <AppBar component="nav">
        <Toolbar>
          <IconButton
            color="inherit"
            aria-label="open drawer"
            edge="start"
            onClick={handleDrawerToggle}
            sx={{ mr: 2, display: { sm: "none" } }}
          >
            <MenuIcon />
          </IconButton>
          <Typography
            variant="h6"
            component="div"
            sx={{ flexGrow: 1, display: { xs: "none", sm: "block" } }}
          >
            <img
              data-testid="main-image"
              src="data:image/svg+xml;base64,"
              alt="GetirYemek"
              shape="NORMAL"
              class="style__Image-sc-__sc-1h9bp23-0 ezAmPr"
            ></img>
          </Typography>
          <Box sx={{ display: { xs: "none", sm: "block" } }}>
            {navItems.map((item) => (
              <Button key={item} sx={{ color: "#fff" }}>
                {item}
              </Button>
            ))}
          </Box>
        </Toolbar>
      </AppBar>
      <Box component="nav">
        <Drawer
          container={container}
          variant="temporary"
          open={mobileOpen}
          onClose={handleDrawerToggle}
          ModalProps={{
            keepMounted: true, // Better open performance on mobile.
          }}
          sx={{
            display: { xs: "block", sm: "none" },
            "& .MuiDrawer-paper": {
              boxSizing: "border-box",
              width: drawerWidth,
              backgroundColor: "black",
              color: "white",
            },
          }}
        >
          {drawer}
        </Drawer>
      </Box>
      <Box component="main">
        <Toolbar />
      </Box>
      <Box
        sx={{ display: { xs: "none", md: "block" } }}
        className=""
        style={{
          position: "absolute",
          zIndex: "4",
          top: "300px",
          left: "100px",
        }}
      >
        <img src="https://getir.com/_next/static/images/bimutluluk-b3a7fcb14fc9a9c09b60d7dc9b1b8fd6.svg"></img>
      </Box>
      <Box
        sx={{ display: { xs: "none", lg: "block" } }}
        style={{
          position: "absolute",
          zIndex: "4",
          top: "500px",
          left: "90px",
        }}
      >
        <Typography
          variant="h6"
          style={{ color: "white", fontWeight: "bold", fontSize: "24px" }}
        >
          Dakikalar İçinde <br /> Kapınızda
        </Typography>
      </Box>

      <Box
        className="rounded d-none d-sm-none d-md-none d-lg-block d-xl-block"
        style={{
          backgroundColor: "white",
          position: "absolute",
          zIndex: "4",
          top: "300px",
          right: "50px",
          padding: "10px",
        }}
      >
        <Typography color="secondary" className="text-center">Giriş yap veya kayıt ol</Typography>
        {/* color direk verilebilir. */}

        {/* <select
          class="form-select form-select-lg mb-3"
          aria-label=".form-select-lg example"
        >
          <option selected>Open this select menu</option>
          <option value="1">One</option>
          <option value="2">Two</option>
          <option value="3">Three</option>
        </select> */}

        <div className="d-flex gap-3 mt-3">
          <ReactFlagsSelect
            countries={Object.keys(phones)}
            customLabels={phones}
            placeholder="Select Language"
            onSelect={(code) => setSelected(code)}
         
            selected={selected}
            className ="p-2"
          />

          <TextField id="outlined-basic" label="Phone Number" variant="outlined" />
        </div>
        <div className="mt-3" style= {{display : "flex" , justifyContent : "center" , width : "100%"}}>
        <button  style= {{ width : "100%"}}  type="button" className="btn btn-warning">Warning</button>
        </div>
      
      </Box>
    </Box>
  );
}

Header.propTypes = {
  /**
   * Injected by the documentation to work in an iframe.
   * You won't need it on your project.
   */
  window: PropTypes.func,
};

export default Header;
